package org.bhavya.practice;

// https://www.hackerrank.com/challenges/reverse-shuffle-merge/problem

import static java.util.stream.Collectors.toList;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Collections;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Stack;

public class ReverseShuffleMerge {

  public static String reverseShuffleMerge(String s) {
    // Write your code here
    List<Character> charsInA = new ArrayList<>();
    Map<Character, Stack<Integer>> charIndexMap = new HashMap<>();
    for (int i = 0; i < s.length(); i++) {
      var ch = s.charAt(i);

      if (charIndexMap.containsKey(ch)) {
        charIndexMap.get(ch).push(i);
      } else {
        var st = new Stack<Integer>();
        st.push(i);
        charIndexMap.put(ch, st);
        charsInA.add(ch);
      }
    }
    charsInA = charsInA.stream().sorted().collect(toList());
//    System.out.println(charIndexMap.get('A').peek());
    var i = 0;
    var charsToDiscoverInA = s.length() / 2;
    var lastIndexOfRevA = s.length();
    StringBuilder result = new StringBuilder();
    var elemtsToRemove = new ArrayList<Integer>();
    while (!charIndexMap.isEmpty()) {
      var charAtI = charsInA.get(i);
      var stck = charIndexMap.get(charAtI);
      if (stck.peek() >= charsToDiscoverInA && stck.peek() < lastIndexOfRevA) {
        if(stck.size() % 2 == 0){
          while(stck.size() > 0) {
            lastIndexOfRevA = stck.pop();
            stck.pop();   // need not to do this this can result in wrong out put
            charsToDiscoverInA--;
            result.append(charAtI);
          }
        }
        else {
          lastIndexOfRevA = stck.pop();
          charsToDiscoverInA--;
          result.append(charAtI);
        }
        elemtsToRemove.add(i);
      }
      if (stck.size() == 0) {
        charIndexMap.remove(charAtI);
      } else if(stck.size() % 2 == 0){
        stck.pop();
      }
      if (charsInA.size() == 1) {
        while (charsToDiscoverInA > 0) {
          result.append(charsInA.get(0));
          charsToDiscoverInA--;
        }
      }
      if (charsToDiscoverInA == 0) {
        break;
      }
      if (++i == charsInA.size()) {
//        charsInA.remove(result.charAt(result.length()-1));
        for (int j = elemtsToRemove.size() - 1; j >= 0; j--) {
          charsInA.remove((int) elemtsToRemove.get(j));
        }
        elemtsToRemove.removeAll(elemtsToRemove);
        Collections.reverse(charsInA);
        i = 0;
      }

    }
    return result.toString();

  }

  public static void main(String[] args) throws IOException {
    BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(System.in));

    String s = bufferedReader.readLine();

    String result = reverseShuffleMerge(s);
    System.out.println(result);
    bufferedReader.close();
  }
}

